pragma solidity >= 0.5.15  < 0.6.0;


interface PotLike {
    function drip() external  returns (uint tmp);
    function join(uint wad) external;
    function exit(uint wad) external;
}

contract VatLike {
    mapping(address => mapping (address => uint)) public can;
}


contract DaiSaving {

    function join(uint wad) external {
        VatLike vat = VatLike(0x11C8d156E1b5FD883E31e9091874F2af80b02775);
        PotLike pot = PotLike(0x19E602E0dC93749Ea7aFa0C88F4693d4C02102D3);
        require(vat.can(address(this), 0x19E602E0dC93749Ea7aFa0C88F4693d4C02102D3) == 1, 'vat not authorized');
        pot.drip();
        // need to authorize the pot contract?
        pot.join(wad);
    }

}
